ica</option>        </select>        <br>        <label for="cpop">Population:</label>        <input type="text" name="cpop" id="cpop" size="10"  value="<?php echo $countrypop ?>" required><br>        <label for="cgovt">Government Type:</label>        <input type="text" name="cgovt" id="cgovt" size="40"  value="<?php echo $countrygovt ?>" required><br>        <label for="cleader">Head of Government:</label>        <input type="text" name="cleader" id="cleader" size="40"  value="<?php echo $countryleader ?>" required><br>        <!--                <label for="ccapital">Capital:</label>                        <input type="text" name="ccapital" id="ccapital" size="30"  value="<?php //echo $countrycapital  ?>"><br>-->        <label for="action">&nbsp;</label>        <input type="submit" name="action" id="action" value="Add Country">       </fieldset>      </form>     </div>    </div>    <aside id="page_aside">     <div>     </div>    </aside>   </div>   <footer id="page_footer">    <div>     <?php include $_SERVER['DOCUMENT_ROOT'] . '/modules/mainfooter.php'; ?>     <p>Last Updated: <?php echo date('j F, Y', getlastmod()); ?></p>    </div>   </footer>  </section> </body></html><?php unset($errors) ?>